import React, { useState, useEffect } from "react";

const URL = 'http://localhost:3000/fuel_stations';

export default function CardDisplay() {
  const [stations, setStations] = useState([]);
  const [currentLocation, setCurrentLocation] = useState(null);

  useEffect(() => {
    fetch(URL)
      .then(response => response.json())
      .then(data => setStations(data));
  }, []);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCurrentLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error("Error getting current location:", error);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  useEffect(() => {
    if (currentLocation) {
      const yourLatitude = currentLocation.lat;
      const yourLongitude = currentLocation.lng;

      const fuelStations = [
        {
          station_name: "Colonie Center - Tesla Supercharger",
          latitude: 42.710356,
          longitude: -73.819109
        },
        {
          station_name: "The Spot Restaurant - Tesla Supercharger",
          latitude: 42.145542,
          longitude: -75.902081
        },
        // Add other fuel stations here
      ];

      const apiEndpoint = "https://maps.googleapis.com/maps/api/distancematrix/json";

      const calculateDistances = async () => {
        const updatedStations = [];

        for (const station of fuelStations) {
          const stationLatitude = station.latitude;
          const stationLongitude = station.longitude;

          const params = {
            origins: `${yourLatitude},${yourLongitude}`,
            destinations: `${stationLatitude},${stationLongitude}`,
            key: "AIzaSyDn7oQa4QsdEXc9GoIv5as8gMS8_uF73_4"
          };

          const queryParams = new URLSearchParams(params).toString();
          const requestUrl = `${apiEndpoint}?${queryParams}`;

          try {
            const response = await fetch(requestUrl);
            const data = await response.json();

            const distance = data.rows[0].elements[0].distance.value;
            updatedStations.push({
              ...station,
              distance
            });
          } catch (error) {
            console.log("Error:", error);
          }
        }

        updatedStations.sort((a, b) => a.distance - b.distance);
        console.log(updatedStations);
      };

      calculateDistances();
    }
  }, [currentLocation]);

  return (
    <>   
      <h1>Card Display</h1>
      {/* Render your stations and distances */}
    </>
  );
}
