          {"\u00A0"}
          <Card.Title>{station.street_address}</Card.Title>
          <br></br>
          <Card.Subtitle>
            <span style={{ fontSize: "16px", fontWeight: "bold" }}>Access</span>
            {": "}
            {station.access_days_time}
          </Card.Subtitle>
          <Card.Subtitle>
            <br></br>
            <span style={{ fontSize: "16px", fontWeight: "bold" }}>
              Distance
            </span>
            {": "}
            {station.distance.toFixed(2)} Miles
          </Card.Subtitle>
          <br></br>
          <Card.Subtitle>
            <span style={{ fontSize: "16px", fontWeight: "bold" }}>
              Pricing
            </span>
            {": "}
            {station.ev_pricing}
          </Card.Subtitle>
          <br></br>
          <Accordion defaultActiveKey="1">
            <Accordion.Item eventKey="0">
              <Accordion.Header>
                {" "}
                <span
                  style={{
                    fontSize: "16px",
                    fontWeight: "bold",
                    textAlign: "center",
                    marginRight: "4px",
                  }}
                >
                  Reviews
                </span>
                {"\u00A0"}
                <Badge pill bg="secondary">
                  {station.comments ? station.comments.length : 0}
                </Badge>
              </Accordion.Header>
              <Accordion.Body>
                {station.comments && station.comments.length > 0 ? (
                  station.comments.map((comment) => (
                    <Alert variant="dark">
                      <Col xs={2} md={2}></Col>
                      <Image src="./thumbnail.png" circle />
                      {"\u00A0" + "\u00A0"}
                      {comment}
                    </Alert>
                  ))
                ) : (
                  <Alert variant="danger">No Reviews Yet</Alert>
                )}
                <br></br>
                <br></br>
                <div className="d-grid gap-2">
                  <Button
                    variant="dark"
                    onClick={handleAddReview}
                    direction="horizontal"
                    justify="center"
                  >
                    Add Review
                  </Button>{" "}
                </div>
              </Accordion.Body>
            </Accordion.Item>
          </Accordion>
          <br></br>

          <div className="d-grid gap-2">
            <Button variant="dark" onClick={handleAddReview}>
              Get Directions
            </Button>{" "}
          </div>
        </Card.Body>
      </Card>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add a Review
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Your Review</h4>
          <Form>
            <Form.Group controlId="exampleForm.ControlTextarea1">
              <Form.Control
                as="textarea"
                rows={3}
                value={comment}
                onChange={handleCommentChange}
                placeholder="Type Something..."
              />
            </Form.Group>{" "}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="dark" onClick={handleAddComment}>
            Submit
          </Button>
          <Button variant="dark" onClick={handleCloseModal}>
            Cancel
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
