import React, { useEffect, useState } from 'react';

const URL = 'http://localhost:3000/fuel_stations';

const CardDisplay = () => {
  const [stations, setStations] = useState([]);
  const [currentLocation, setCurrentLocation] = useState(null);
  const [isLocationLoaded, setLocationLoaded] = useState(false);

  useEffect(() => {
    fetch(URL)
      .then(response => response.json())
      .then(data => setStations(data));
  }, []);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCurrentLocation({ lat: latitude, lng: longitude });
          setLocationLoaded(true);
        },
        (error) => {
          console.error("Error getting current location:", error);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  useEffect(() => {
    if (isLocationLoaded && currentLocation) {
      // Your location coordinates
      const yourLatitude = currentLocation.lat;
      const yourLongitude = currentLocation.lng;

      // Calculate distances for each fuel station
      const updatedFuelStations = stations.map(station => {
        const stationLatitude = station.latitude;
        const stationLongitude = station.longitude;

        // Calculate the distance between your location and the fuel station using the Haversine formula
        const distance = calculateDistance(
          yourLatitude,
          yourLongitude,
          stationLatitude,
          stationLongitude
        );

        // Add the distance to the fuel station data
        return {
          ...station,
          distance: distance
        };
      });

      // Sort the fuel stations based on distance
      updatedFuelStations.sort((a, b) => a.distance - b.distance);

      // Update the state with the sorted fuel stations
      setStations(updatedFuelStations);
    }
  }, [isLocationLoaded, currentLocation, stations]);

  // Function to calculate the distance between two coordinates using the Haversine formula
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 3959; // Radius of the Earth in miles
    const dLat = deg2rad(lat2 - lat1);
    const dLon = deg2rad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * Math.sin(dLon / 2) * Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // Distance in miles
    return distance;
  };

  // Function to convert degrees to radians
  const deg2rad = deg => {
    return deg * (Math.PI / 180);
  };

  return (
    <div>
      {stations.map(station => (
        <div key={station.station_name}>
          <p>Station: {station.station_name}</p>
          {station.distance && <p>Distance: {station.distance.toFixed(2)} miles</p>}
        </div>
      ))}
    </div>
  );
};

export default CardDisplay;
