import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Modal from "react-bootstrap/Modal";
import Accordion from "react-bootstrap/Accordion";
import Badge from "react-bootstrap/Badge";
import Alert from "react-bootstrap/Alert";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import { GoogleMap, Marker, InfoWindowF } from "@react-google-maps/api";


const URL = "http://localhost:3000/fuel_stations";

export default function Individualcard({ station, stations, setStations }) {
  const [showModal, setShowModal] = useState(false);
  const [comment, setComment] = useState("");
  const [currentLocation, setCurrentLocation] = useState(null);
  const [selectedStation, setSelectedStation] = useState(null);
  const [distance, setDistance] = useState(null);
  const [duration, setDuration] = useState(null);

  // useEffect(() => {
  //   if (navigator.geolocation) {
  //     navigator.geolocation.getCurrentPosition(
  //       (position) => {
  //         const { latitude, longitude } = position.coords;
  //         setCurrentLocation({ lat: latitude, lng: longitude });
  //       },
  //       (error) => {
  //         console.error("Error getting current location:", error);
  //       }
  //     );
  //   } else {
  //     console.error("Geolocation is not supported by this browser.");
  //   }
  // }, []);

  useEffect(() => {
    if (selectedStation && currentLocation) {
      const directionsService = new window.google.maps.DirectionsService();
      const request = {
        origin: currentLocation,
        destination: {
          lat: selectedStation.latitude,
          lng: selectedStation.longitude,
        },
        travelMode: "DRIVING",
        drivingOptions: {
          departureTime: new Date(),
        },
      };
      directionsService.route(request, handleDirectionsResponse);
    }
  }, [selectedStation, currentLocation]);

  function handleSelectedStation(station) {
    setSelectedStation(station);
  }

  function handleCloseInfoWindow() {
    setSelectedStation(null);
  }

  function handleDirectionsResponse(response, status) {
    if (status === "OK") {
      const leg = response.routes[0].legs[0];
      setDistance(leg.distance.text);
      setDuration(leg.duration.text);
    } else {
      console.error("Directions request failed:", status);
      setDistance(null);
      setDuration(null);
    }
  }

  function handleGetDirections() {
    if (currentLocation && selectedStation) {
      const origin = `${currentLocation.lat},${currentLocation.lng}`;
      const destination = `${selectedStation.latitude},${selectedStation.longitude}`;
      const directionsUrl = `https://www.google.com/maps/dir/?api=1&origin=${origin}&destination=${destination}`;
      window.open(directionsUrl, "_blank");
    }
  }

  function handleCommentChange(event) {
    setComment(event.target.value);
  }

  function handleAddReview() {
    setShowModal(true);
  }

  function handleCloseModal() {
    setComment("");
    setShowModal(false);
  }

  function handleAddComment(e) {
    e.preventDefault();
    const updatedComments = station.comments
      ? [...station.comments, comment]
      : [comment];
    const updatedStation = {
      ...station,
      comments: updatedComments,
    };

    fetch(`${URL}/${station.id}`, {
      method: "PATCH",
      headers: {
        "Content-type": "application/json",
        Accept: "application/json",
      },
      body: JSON.stringify({ comments: updatedComments }),
    })
      .then((response) => response.json())
      .then((data) => {
        // Find the index of the updated station in the stations array
        const updatedIndex = stations.findIndex((s) => s.id === station.id);

        // Create a new stations array with the updated station
        const updatedStations = [
          ...stations.slice(0, updatedIndex),
          updatedStation,
          ...stations.slice(updatedIndex + 1),
        ];

        // Update the stations state with the updatedStations array
        setStations(updatedStations);
      })
      .catch((error) => {
        console.log("Error:", error);
      });

    setShowModal(false);
    setComment("");
  }
  const containerStyle = {
    width: "23vw",
    height: "25vh",
  };

  return (
    <>
      <Card bg="white" style={{ flex: "1" }}>
        <Card.Header
          style={{
            textAlign: "center",
            fontSize: "22px",
            fontFamily: "Helvetica Neue",
            fontStyle: "normal",
            backgroundColor: "black",
            color: "white",
          }}
        >
          {" "}
          {station.station_name.split(" - Tesla Supercharger")}
        </Card.Header>

        <Card.Body>
          {/* <iframe
            title="Google Map"
            src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d12097.33410972836!2d-74.0182606!3d40.7106737!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2sus!4v1687553644224!5m2!1sen!2sus"
            width="100%"
            height="300"
            // style={{ border: }}
            allowFullScreen=""
            loading="lazy"
            referrerPolicy="no-referrer-when-downgrade"
          ></iframe> */}

<GoogleMap
      mapContainerStyle={containerStyle}
      center={currentLocation}
      zoom={13}
    >
      {currentLocation && (
        <Marker
          //position={currentLocation}
          title="Current Location"
          icon={{
            url: "https://maps.google.com/mapfiles/ms/icons/blue-dot.png",
          }}
        />
      )}
      {/* {stations.map(station => (
        <Marker
          key={station.id}
          position={{ lat: station.latitude, lng: station.longitude }}
          title={station.station_name}
          onClick={() => handleSelectedStation(station)}
        />
      ))} */}

{stations.map(station => (
        <Marker
          key={station.id}
          position={{ lat: selectedStation.latitude, lng: selectedStation.longitude }}
          title={station.station_name}
          onClick={() => handleSelectedStation(station)}
        />
      ))}
      {selectedStation && (
        <InfoWindowF
          position={{
            lat: selectedStation.latitude,
            lng: selectedStation.longitude,
          }}
          onCloseClick={handleCloseInfoWindow}
          visible={selectedStation !== null}
        >
          <div>
            <h3>City: {selectedStation.city}</h3>
            <p>Station Name: {selectedStation.station_name.split(" - Tesla Supercharger")}</p>
            <p>Address: {selectedStation.street_address}</p>
            <p>Phone: {selectedStation.station_phone}</p>
            {distance && duration && (
              <p>
                Distance: {distance} | Duration: {duration}
              </p>
            )}
            <button onClick={handleGetDirections}>Get Directions</button>
          </div>
        </InfoWindowF>
      )}
    </GoogleMap>
          {"\u00A0"}
          <Card.Title>{station.street_address}</Card.Title>
          <br></br>
          <Card.Subtitle>
            <span style={{ fontSize: "16px", fontWeight: "bold" }}>Access</span>
            {": "}
            {station.access_days_time}
          </Card.Subtitle>
          <Card.Subtitle>
            <br></br>
            <span style={{ fontSize: "16px", fontWeight: "bold" }}>
              Distance
            </span>
            {": "}
            {station.distance.toFixed(2)} Miles
          </Card.Subtitle>
          <br></br>
          <Card.Subtitle>
            <span style={{ fontSize: "16px", fontWeight: "bold" }}>
              Pricing
            </span>
            {": "}
            {station.ev_pricing}
          </Card.Subtitle>
          <br></br>
          <Accordion defaultActiveKey="1">
            <Accordion.Item eventKey="0">
              <Accordion.Header>
                {" "}
                <span
                  style={{
                    fontSize: "16px",
                    fontWeight: "bold",
                    textAlign: "center",
                    marginRight: "4px",
                  }}
                >
                  Reviews
                </span>
                {"\u00A0"}
                <Badge pill bg="secondary">
                  {station.comments ? station.comments.length : 0}
                </Badge>
              </Accordion.Header>
              <Accordion.Body>
                {station.comments && station.comments.length > 0 ? (
                  station.comments.map((comment) => (
                    <Alert variant="dark">
                      <Col xs={2} md={2}></Col>
                      <Image src="./thumbnail.png" circle />
                      {"\u00A0" + "\u00A0"}
                      {comment}
                    </Alert>
                  ))
                ) : (
                  <Alert variant="danger">No Reviews Yet</Alert>
                )}
                <br></br>
                <br></br>
                <div className="d-grid gap-2">
                  <Button
                    variant="dark"
                    onClick={handleAddReview}
                    direction="horizontal"
                    justify="center"
                  >
                    Add Review
                  </Button>{" "}
                </div>
              </Accordion.Body>
            </Accordion.Item>
          </Accordion>
          <br></br>

          <div className="d-grid gap-2">
            <Button variant="dark" onClick={handleAddReview}>
              Get Directions
            </Button>{" "}
          </div>
        </Card.Body>
      </Card>

      <Modal
        show={showModal}
        onHide={handleCloseModal}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add a Review
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Your Review</h4>
          <Form>
            <Form.Group controlId="exampleForm.ControlTextarea1">
              <Form.Control
                as="textarea"
                rows={3}
                value={comment}
                onChange={handleCommentChange}
                placeholder="Type Something..."
              />
            </Form.Group>{" "}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="dark" onClick={handleAddComment}>
            Submit
          </Button>
          <Button variant="dark" onClick={handleCloseModal}>
            Cancel
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}
