import { useState, useEffect } from "react";
import PlacesAutocomplete, {
  geocodeByAddress,
  getLatLng,
} from "react-places-autocomplete";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

export default function Search({
  currentLocation,
  setLat,
  setLng,
  address,
  setAddress,
}) {
  useEffect(() => {
    if (currentLocation) {
      setLat(currentLocation.lat);
      setLng(currentLocation.lng);
    }
  }, [currentLocation]);

  const handleAddressSelect = async (selectedAddress) => {
    setAddress(selectedAddress);

    try {
      const results = await geocodeByAddress(selectedAddress);
      const selectedLocation = await getLatLng(results[0]);
      const { lat, lng } = selectedLocation;
      console.log(`Latitude: ${lat}, Longitude: ${lng}`);
      setLat(lat);
      setLng(lng);
      const addressArr = selectedAddress.split(", ");
    } catch (error) {
      console.log("Error:", error);
    }
  };

  function handleAddressChange(selectedAddress) {
    //console.log(address,"   parent")
    if (address.length === 1) {
      //console.log ("DONE")
      setLat(currentLocation.lat);
      setLng(currentLocation.lng);
    }
    setAddress((prevAddr) => selectedAddress);
  }

  return (
    <>
      <Form.Group controlId="addressCityStateZip" 


      >

        <div>
          <PlacesAutocomplete 
            value={address}
            onChange={handleAddressChange}
            onSelect={handleAddressSelect}
          >
            {({
              getInputProps,
              suggestions,
              getSuggestionItemProps,
              loading,
            }) => (
              <div>
                <Form.Control
                  {...getInputProps({
                    placeholder: "Address",
                    size: "sm",
                  })}
                  style={{ width: "50%" }}
                />
                <div>
                  {loading && <div>Loading...</div>}
                  {suggestions.map((suggestion) => {
                    const style = {
                      backgroundColor: suggestion.active ? "#e2e2e2" : "#fff",
                    };
                    return (
                      <div
                        {...getSuggestionItemProps(suggestion, { style })}
                        key={suggestion.placeId}
                      >
                        {suggestion.description}
                      </div>
                    );
                  })}
                </div>
              </div>
            )}
          </PlacesAutocomplete>
        </div>
      </Form.Group>
    </>
  );
}
