import React, { useEffect,useState } from "react";
import { v4 as uuidv4 } from "uuid";
import Individualcard from "./Individualcard";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';


function CardDisplay({
  stations,
  currentLocation,
  isLocationLoaded,
  setStations,
  searchLocation, 
  setsearchLocation,
  testLocation, 
  setTestLocation 
}) {
  //console.log(currentLocation)
  // const [testLocation, setTestLocation] = useState({lat: currentLocation.lat, lng: currentLocation.lng})
  // console.log(testLocation)
  //const testLocation={...currentLocation}



  const calculateDistances = () => {
    //original 
    // const yourLatitude = currentLocation.lat;
    // const yourLongitude = currentLocation.lng;

    const yourLatitude = testLocation.lat;
    const yourLongitude = testLocation.lng;


    // const yourLatitude = testLocation?.lat || 0;
    // const yourLongitude = testLocation?.lng || 0;

  // if (yourLatitude === undefined || yourLongitude === undefined) {
  //   // Handle the case when testLocation is null or undefined
  //   // Return an appropriate value or show an error message
  //   return null;
  // }


    console.log(testLocation)



    return stations
      .map((station) => {
        const stationLatitude = station.latitude;
        const stationLongitude = station.longitude;

        const distance = calculateDistance(
          yourLatitude,
          yourLongitude,
          stationLatitude,
          stationLongitude
        );

        return {
          ...station,
          distance: distance,
        };
      })
      .sort((a, b) => a.distance - b.distance)
      .slice(0, 32);
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 3959; // Radius of the Earth in miles
    const dLat = deg2rad(lat2 - lat1);
    const dLon = deg2rad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(lat1)) *
        Math.cos(deg2rad(lat2)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // Distance in miles
    return distance;
  };

  const deg2rad = (deg) => {
    return deg * (Math.PI / 180);
  };

  const closestStations =
    isLocationLoaded && currentLocation ? calculateDistances() : [];


    function handleSearch(e){
      setsearchLocation(e.target.value)
    }

    function handleOptionChange(e){

    }

  return (
    <div>
      <br></br><br></br><br></br>
      <InputGroup className="mb-3">
        <Form.Control
          placeholder="Recipient's username"
          aria-label="Recipient's username"
          aria-describedby="basic-addon2"
          value ={searchLocation}
          onChange={handleSearch} />

        <InputGroup.Text id="basic-addon2">@example.com</InputGroup.Text>
      </InputGroup>
       <PlacesAutocomplete
                  value={address}
                  onChange={handleAddressChange}
                  onSelect={handleAddressSelect}
                  required
                  isInvalid
                >
                  {({
                    getInputProps,
                    suggestions,
                    getSuggestionItemProps,
                    loading,
                  }) => (
                    <div>
                      <Form.Control
                        required={!!suggestions.length}
                        isInvalid={
                          suggestions.length > 0 &&
                          !isNaN(parseFloat(suggestions)) &&
                          isFinite(suggestions)
                        }
                        type="text"
                        {...getInputProps({
                          placeholder: "Address",
                          size: "sm",
                        })}
                        style={{ width: "100%" }}
                      />
                      <Form.Control.Feedback type="invalid">
                        Please Enter a Valid Address
                      </Form.Control.Feedback>

                      <div>
                        {loading && <div>Loading...</div>}
                        {suggestions.map((suggestion) => {
                          const style = {
                            backgroundColor: suggestion.active
                              ? "#e2e2e2"
                              : "#fff",
                          };
                          return (
                            <div
                              {...getSuggestionItemProps(suggestion, { style })}
                              key={suggestion.placeId}
                            >
                              {suggestion.description}
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  )}
                </PlacesAutocomplete>
      <Row
        xs={1}
        md={4}
        className="g-4"
        style={{
          marginLeft: "100px",
          marginRight: "100px",
          display: "flex",
        }}
      >
        {closestStations.map((station) => (
          <Col key={station}>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                height: "100%",
              }}
            >
              <Individualcard
                key={station.id}
                station={station}
                stations={stations}
                setStations={setStations}
                stationLatitude={station.latitude}
                stationLongitude={station.longitude}
              />
            </div>
          </Col>
        ))}
      </Row>
      <br></br>
    </div>
  );
}

export default CardDisplay;
