import React from "react";
import { useState,useEffect } from "react";

const URL = 'http://localhost:3000/fuel_stations';

export default function Carddisplay() {

  const [stations, setStations] = useState([]);
  const [currentLocation, setCurrentLocation] = useState(null);

  useEffect(() => {
    fetch(URL)
      .then(response => response.json())
      .then(data => setStations(data));
  }, []);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setCurrentLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.error("Error getting current location:", error);
        }
      );
    } else {
      console.error("Geolocation is not supported by this browser.");
    }
  }, []);

  const axios = require('axios');

  // Your location coordinates
  const yourLatitude = currentLocation.lat;
  const yourLongitude = currentLocation.lng;

  // Fuel stations data (assuming it's stored in an array called 'fuelStations')
  const fuelStations = [
    {
      station_name: "Colonie Center - Tesla Supercharger",
      latitude: 42.710356,
      longitude: -73.819109
    },
    {
      station_name: "The Spot Restaurant - Tesla Supercharger",
      latitude: 42.145542,
      longitude: -75.902081
    },
    // Add other fuel stations here
  ];

  // Google Maps Distance Matrix API endpoint
  const apiEndpoint = "https://maps.googleapis.com/maps/api/distancematrix/json";

  // Calculate distances for each fuel station
  fuelStations.forEach(station => {
    // Get the station's latitude and longitude
    const stationLatitude = station.latitude;
    const stationLongitude = station.longitude;

    // Make a request to the Google Maps Distance Matrix API
    const params = {
      origins: `${yourLatitude},${yourLongitude}`,
      destinations: `${stationLatitude},${stationLongitude}`,
      key: "YOUR_GOOGLE_MAPS_API_KEY"
    };

    axios.get(apiEndpoint, { params })
      .then(response => {
        // Parse the response and extract the distance
        const distance = response.data.rows[0].elements[0].distance.value;

        // Add the distance to the fuel station data
        station.distance = distance;
      })
      .catch(error => {
        console.log("Error:", error);
      });
  });

  // Wait for all distance calculations to finish
  Promise.all(fuelStations.map(station => station.distance))
    .then(() => {
      // Sort the fuel stations based on distance
      fuelStations.sort((a, b) => a.distance - b.distance);

      // Print the fuel stations sorted by distance
      fuelStations.forEach(station => {
        console.log(`Station: ${station.station_name}`);
        console.log(`Distance: ${station.distance} meters`);
        console.log();
      });
    });


  console.log (stations)

  return (
    <>   
      <h1>card display</h1>
    </>
  );
}
