import React, { useState } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import PlacesAutocomplete, { geocodeByAddress, getLatLng } from 'react-places-autocomplete';

const stateAbbreviations = [
    'AL', 'AK', 'AZ', 'AR', 'CA', 'CO', 'CT', 'DE', 'FL', 'GA', 'HI', 'ID', 'IL', 'IN', 'IA', 'KS', 'KY', 'LA', 'ME', 'MD',
    'MA', 'MI', 'MN', 'MS', 'MO', 'MT', 'NE', 'NV', 'NH', 'NJ', 'NM', 'NY', 'NC', 'ND', 'OH', 'OK', 'OR', 'PA', 'RI', 'SC',
    'SD', 'TN', 'TX', 'UT', 'VT', 'VA', 'WA', 'WV', 'WI', 'WY'
  ];

function Newstation() {
  const [stationName, setStationName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [state, setState] = useState('');
  const [pricing, setPricing] = useState('');
  const [review, setReview] = useState([]);
  const [chargerType, setChargerType] = useState('');
  const [lat,setLat] = useState(0)
  const [lng,setLng] = useState(0)
  const [accessDaysTime,setAccessDayTime] = useState('')
  const [zip,setZip] = useState('') 

  function handleAddressChange (selectedAddress) {
    setAddress(selectedAddress);
  };

  const handleAddressSelect = async (selectedAddress) => {
    setAddress(selectedAddress);

    try {
        const results = await geocodeByAddress(selectedAddress);
        const selectedLocation = await getLatLng(results[0]);
        const { lat, lng } = selectedLocation;
        console.log(`Latitude: ${lat}, Longitude: ${lng}`);
        setLat(lat)
        setLng(lng)
        const addressArr = selectedAddress.split(', ')
        setCity (addressArr[addressArr.length-3])
        setState (addressArr[addressArr.length-2])

        } catch (error) {
            console.log('Error:', error);
        }
    };

    function handleSubmit (event) {
        event.preventDefault();

        const newStation = {
            "access_days_time": accessDaysTime,
            "station_name": stationName,
            "station_phone": "877-798-3752",
            "latitude": lat,
            "longitude": lng,
            "city": city,
            "state": state,
            "street_address": address.split(',')[0],
            "country": 'US',
            "zip": zip,
            "comments": review
        }

        fetch ('http://localhost:3000/fuel_stations', {
            method: "POST",
            headers: {
                "Content-type": "application/json",
                "Accept": "application/json"
            },
            body: JSON.stringify(newStation)
        })
        .then (response => response.json())
        .then (data => {
            console.log (newStation)
            setStationName('');
            setAddress('');
            setCity('');
            setState('');
            setPricing('');
            setReview('');
            setChargerType('');
            setAccessDayTime('')
            setZip('')
        })
    };

    return (
        <div>
            <h2>Add New Station</h2>
            <Form onSubmit={handleSubmit}>
            <Form.Group controlId="stationName">
                <Form.Label>Station Name</Form.Label>
                <Form.Control
                type="text"
                size="sm"
                value={stationName}
                onChange={(e) => setStationName(e.target.value)}
                style={{ width: '50%' }}
                />
            </Form.Group>

            <Form.Group controlId="addressCityStateZip">
                <Form.Label>Address, City, State</Form.Label>
                <div>
                    <div>
                    <PlacesAutocomplete
                        value={address}
                        onChange={handleAddressChange}
                        onSelect={handleAddressSelect}
                    >
                        {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                        <div>
                            <Form.Control
                            {...getInputProps({
                                placeholder: 'Address',
                                size: 'sm',
                            })}
                            style={{ width: '50%' }}
                            />
                            <div>
                            {loading && <div>Loading...</div>}
                            {suggestions.map((suggestion) => {
                                const style = {
                                backgroundColor: suggestion.active ? '#e2e2e2' : '#fff',
                                };
                                return (
                                <div
                                    {...getSuggestionItemProps(suggestion, { style })}
                                    key={suggestion.placeId}
                                >
                                    {suggestion.description}
                                </div>
                                );
                            })}
                            </div>
                        </div>
                        )}
                    </PlacesAutocomplete>
                    </div>
                        <div style={{ display: 'flex', marginTop: '10px' }}>
                            <div style={{ flex: 1, marginRight: '5px' }}>
                                <Form.Control
                                    type="text"
                                    size="sm"
                                    placeholder="City"
                                    value={city}
                                    onChange={(e) => setCity(e.target.value)}
                                    style={{ width: '50%' }}
                                />
                            </div>
                            <div style={{ flex: 1, marginRight: '5px' }}>
                                <Form.Control
                                    type="text"
                                    size="sm"
                                    placeholder="Zip"
                                    value={zip}
                                    onChange={(e) => setZip(e.target.value)}
                                    style={{ width: '20%' }}
                                />
                            </div>
                            <div style={{ flex: 1, marginLeft: '5px' }}>
                                <Form.Control
                                    as="select"
                                    size="sm"
                                    value={state}
                                    onChange={(e) => setState(e.target.value)}
                                    style={{ width: '25%' }}
                                >
                                    <option value="">State</option>
                                    {stateAbbreviations.map((state) => (
                                        <option value={state} key={state}>
                                        {state}
                                        </option>
                                    ))}
                                </Form.Control>
                        </div>
                    </div>
                </div>
            </Form.Group>

            <Form.Group controlId="pricing">
                <Form.Label>Pricing</Form.Label>
                <Form.Control
                    type="text"
                    size="sm"
                    value={pricing}
                    onChange={(e) => setPricing(e.target.value)}
                    style={{ width: '50%' }}
                />
            </Form.Group>

            <Form.Group controlId="accessdaytime">
                <Form.Label>Access Houres</Form.Label>
                <Form.Control
                    type="text"
                    size="sm"
                    value={accessDaysTime}
                    onChange={(e) => setAccessDayTime(e.target.value)}
                    style={{ width: '50%' }}
                />
            </Form.Group>

            <Form.Group controlId="chargerType">
                <Form.Label>Charger Type</Form.Label>
                <Form.Control
                    type="text"
                    size="sm"
                    value={chargerType}
                    onChange={(e) => setChargerType(e.target.value)}
                    style={{ width: '50%' }}
                />
            </Form.Group>

            <Form.Group controlId="review">
                <Form.Label>Review/Comment</Form.Label>
                <Form.Control
                    as="textarea"
                    rows={3}
                    size="sm"
                    value={review}
                    onChange={(e) => setReview([e.target.value])}
                    style={{ width: '50%' }}
                />
            </Form.Group>

            <Button variant="primary" type="submit">
                Submit
            </Button>
        </Form>
        </div>
  );
}

export default Newstation;
