import Carddisplay from "../components/carddisplay"
import { useEffect,useState } from "react";

const URL = 'http://localhost:3000/fuel_stations';

export default function AllChargers() {
    const [stations, setStations] = useState([]);
    const [currentLocation, setCurrentLocation] = useState(null);
    const [isLocationLoaded, setLocationLoaded] = useState(false);
    const [searchLocation,setSearchLocation] = useState('')

    useEffect(() => {
        fetch(URL)
          .then(response => response.json())
          .then(data => setStations(data));
      }, []);

      useEffect(() => {
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              const { latitude, longitude } = position.coords;
              setCurrentLocation({ lat: latitude, lng: longitude });
              setLocationLoaded(true);
            },
            (error) => {
              console.error("Error getting current location:", error);
            }
          );
        } else {
          console.error("Geolocation is not supported by this browser.");
        }
      }, []);

      function handleSearch (e) {
        // console.log (e.target.value)
        setSearchLocation((prevSearch) => e.target.value)
        console.log(searchLocation)
      }

    return (
        <>
            <Carddisplay stations={stations.filter((station) => station.street_address.toLowerCase().includes(searchLocation))} searchLocation={searchLocation} handleSearch={handleSearch} currentLocation={currentLocation} isLocationLoaded={isLocationLoaded} setStations={setStations}/>
        </>
    )
}
