import React from "react";
import { v4 as uuidv4 } from "uuid";
import Individualcard from "./individualcard";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import { useState } from "react";

function CardDisplay({
  stations,
  currentLocation,
  isLocationLoaded,
  setStations,
  handleSearch,
  searchLocation
}) {

  const calculateDistances = () => {
    const yourLatitude = currentLocation.lat;
    const yourLongitude = currentLocation.lng;

    return stations
      .map((station) => {
        const stationLatitude = station.latitude;
        const stationLongitude = station.longitude;

        const distance = calculateDistance(
          yourLatitude,
          yourLongitude,
          stationLatitude,
          stationLongitude
        );

        return {
          ...station,
          distance: distance,
        };
      })
      .sort((a, b) => a.distance - b.distance)
      .slice(0, 32);
  };

  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 3959; // Radius of the Earth in miles
    const dLat = deg2rad(lat2 - lat1);
    const dLon = deg2rad(lon2 - lon1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(lat1)) *
        Math.cos(deg2rad(lat2)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const distance = R * c; // Distance in miles
    return distance;
  };

  const deg2rad = (deg) => {
    return deg * (Math.PI / 180);
  };

  const closestStations =
    isLocationLoaded && currentLocation ? calculateDistances() : [];


  function handleOptionChange () {

  }

  const handleAddressSelect = async (selectedAddress) => {
    setAddress(selectedAddress);

    try {
        const results = await geocodeByAddress(selectedAddress);
        const selectedLocation = await getLatLng(results[0]);
        const { lat, lng } = selectedLocation;
        console.log(`Latitude: ${lat}, Longitude: ${lng}`);
        setLat(lat)
        setLng(lng)
        const addressArr = selectedAddress.split(', ')
        setCity (addressArr[addressArr.length-3])
        setState (addressArr[addressArr.length-2])

        } catch (error) {
            console.log('Error:', error);
        }
    };

  return (
    <div>
      <br></br><br></br><br></br>
      <Form.Group controlId="addressCityStateZip">
          <Form.Label>Address</Form.Label>
          <div>
              <PlacesAutocomplete
                  value={address}
                  onChange={handleAddressChange}
                  onSelect={handleAddressSelect}
              >
                  {({ getInputProps, suggestions, getSuggestionItemProps, loading }) => (
                  <div>
                      <Form.Control
                      {...getInputProps({
                          placeholder: 'Address',
                          size: 'sm',
                      })}
                      style={{ width: '50%' }}
                      />
                      <div>
                      {loading && <div>Loading...</div>}
                      {suggestions.map((suggestion) => {
                          const style = {
                          backgroundColor: suggestion.active ? '#e2e2e2' : '#fff',
                          };
                          return (
                          <div
                              {...getSuggestionItemProps(suggestion, { style })}
                              key={suggestion.placeId}
                          >
                              {suggestion.description}
                          </div>
                          );
                      })}
                      </div>
                  </div>
                  )}
              </PlacesAutocomplete>

          </div>
      </Form.Group>


      <Form onSubmit={handleSearch}>
        <Row className="align-items-center">
          <Col md={6}>
            <Form.Control
              type="text"
              placeholder="Search"
              value={searchLocation}
              onChange={(e) => handleSearch(e)}
            />
          </Col>
          <Col md={4}>
            <Form.Select onChange={(e) => handleOptionChange(e.target.value)}>
              <option value="name">Name</option>
              <option value="address">Address</option>
            </Form.Select>
          </Col>
          <Col md={2}>
            <Button type="submit">Search</Button>
          </Col>
        </Row>
      </Form>
      <br></br>
      <Row
        xs={1}
        md={4}
        className="g-4"
        style={{
          marginLeft: "100px",
          marginRight: "100px",
          display: "flex",
        }}
      >
        {closestStations.map((station) => (
          <Col key={station}>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                height: "100%",
              }}
            >
              <Individualcard
                key={station.id}
                station={station}
                stations={stations}
                setStations={setStations}
                stationLatitude={station.latitude}
                stationLongitude={station.longitude}
              />
            </div>
          </Col>
        ))}
      </Row>
      <br></br>
    </div>
  );
}

export default CardDisplay;
